CREATE TABLE IF NOT EXISTS partner( id BIGSERIAL PRIMARY KEY, name VARCHAR(255) NOT NULL, active BOOLEAN NOT NULL, registered_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS partner_config_map( id BIGSERIAL PRIMARY KEY, partner_id BIGINT NOT NULL, config_mapping_id BIGINT NOT NULL, value VARCHAR(255), created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP);

create table partner_aud (id int8 not null, rev int4 not null, revtype int2, active boolean, name varchar(255), registered_at timestamp, updated_at timestamp, primary key (id, rev));
alter table partner_aud add constraint FKu14stet717iktedsoqw1jfm8 foreign key (rev) references revinfo;

CREATE TABLE IF NOT EXISTS benefit_reconciliation_tracker( id BIGSERIAL PRIMARY KEY, transaction_id BIGINT, request_message_id VARCHAR(100), reconciliation_message_id VARCHAR(25), response TEXT, transaction_status VARCHAR(4), pfms_id VARCHAR(50), pfms_transaction_id VARCHAR(16), instruction_id VARCHAR(30), rejection_reason_code VARCHAR(10), rejection_reason_narration VARCHAR(100), pfms_transaction_date TIMESTAMP, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, response_fetched BOOLEAN DEFAULT FALSE, response_processed BOOLEAN DEFAULT FALSE);
CREATE TABLE IF NOT EXISTS benefit_reconciliation_tracker_archive( id BIGSERIAL PRIMARY KEY, benefit_reconciliation_tracker_id BIGINT, transaction_id BIGINT, request_message_id VARCHAR(100), reconciliation_message_id VARCHAR(25), response TEXT, transaction_status VARCHAR(4), pfms_id VARCHAR(50), pfms_transaction_id VARCHAR(16), instruction_id VARCHAR(30), rejection_reason_code VARCHAR(10), rejection_reason_narration VARCHAR(100), pfms_transaction_date TIMESTAMP, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, response_fetched BOOLEAN DEFAULT FALSE, response_processed BOOLEAN DEFAULT FALSE, archived_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP);