alter table if exists client_requests add column response_payload varchar(255)
create table trigger (id int8 generated by default as identity, active boolean, class_name varchar(255), client_id int8, cron_time varchar(255), event_id int8, event_name varchar(255), extra_params varchar(255), function_name varchar(255), primary key (id))
alter table if exists subscriber_url add column request_method varchar(255);