UPDATE episode SET disease_id = (SELECT id FROM disease_template WHERE disease_name = 'Drug Sensitive Tuberculosis'), start_date = 'UTC_DATE_MINUS_2_DAY', end_date = 'UTC_DATE_PLUS_166_DAY', current_stage_id = (SELECT id FROM stages WHERE stage_name = 'DIAGNOSED_ON_TREATMENT_PRIVATE') WHERE id = (SELECT MAX(id) FROM episode);
UPDATE episode_stage SET end_date = 'UTC_DATE_TIME_PLUS_5_MINUTE' WHERE id = (SELECT es.id FROM episode_stage es INNER JOIN stages s on s.id = es.stage_id AND es.episode_id = (SELECT MAX(id) FROM episode) AND s.stage_name = 'PRESUMPTIVE_OPEN_PRIVATE');